import React, { useState } from "react";
import data from "./data";
import Navbar from "./Navbar"

const Search = () => {
    const [filter, setFilter] = useState('');

    const searchText = (event) =>{
        setFilter(event.target.value);
    }
    let dataSearch = data.productData.filter(item =>{
        // return Object.keys(item).some(key =>
        //     item[key].toString().toLowerCase().include(filter.toString().toLowerCase())
        //     )
    });
    return(
        <>
        <Navbar/>
        <section className="py-4 container">
            <div className="row justify-content-center">
                <div className="col-12 mb-5">
                    <div className="mb-3 col-4 mx-auto text-center">
                        <label className="form-label h4">Search</label>
                        <input
                                type="text"
                                className="form-control"
                                value={filter}
                                onChange={searchText.bind(this)}
                        />
                        </div>
                        </div>

                        {dataSearch.map((item,index)=>{
                            return(
                                <div className="col-11 col-md-6 col-lg-3 mx-o mb-4">
                                    <div className="card p-0 overflow-hidden h-100 shadow">
                                        <img src={item.img} className="card-img-top img-fluid" />
                                        <div className="card-body">
                                            <h5 className="card-title">{item.title}</h5>
                                            <p className="card-text">{item.desc}</p>
                                        </div>
                                    </div>
                                </div>

                            )
                        })}

                </div>
        </section>
        </>
    )
}

export default Search;
